class App extends Component {
  state = {
    persons: [
      {name: 'sven_1', age: 30},
      {name: 'sven_2', age: 31},
      {name: 'sven_3', age: 32}
    ]
  }

  swtchNameHandler = () => {
    console.log("CLicked SwitchName Handler");
  }

  render(){
    return  (
          <div className="App">

            <button onClick={this.swtchNameHandler} >Switch Name</button>
            <Person name={this.state.persons[0].name} age={this.state.persons[0].age} />
            <Person name={this.state.persons[1].name} age={this.state.persons[1].age}>My Hobbies are: drumming and programming</Person>
            <Person name={this.state.persons[2].name} age={this.state.persons[2].age} />
      </div>
    );
  }
}

export default App;
