import React, { useState } from "react";
// import React, { Component } from "react";
import "./App.css";
import Person from "./Person/Person";

const App = props => {
  // array destructiring [ state, reference function to set new state]
  const [personsState, setPersonsState] = useState({
    persons: [
      { name: "sven_1", age: 30 },
      { name: "sven_2", age: 31 },
      { name: "sven_3", age: 32 }
    ],
    otherState: "other state man !"
  });

  swtchNameHandler = () => {
    console.log("CLicked SwitchName Handler");
    // this.state.persons[0].name = 'sven_new_1';
    this.setState( {
      persons: [
        { name: "sven_1_new", age: 30 },
        { name: "sven_2", age: 31 },
        { name: "sven_3", age: 32 }
      ]
    } );
  };

  return (
    <div className="App">
      <button onClick={this.swtchNameHandler}>Switch Name</button>
      <Person name={this.state.persons[0].name} age={this.state.persons[0].age} />
      <Person name={this.state.persons[1].name} age={this.state.persons[1].age} >
        My Hobbies are: drumming and programming
      </Person>
      <Person
        name={this.state.persons[2].name}
        age={this.state.persons[2].age}
      />
    </div>
  );
};

export default App;

